$(document).ready(function(){
	//Modal
		$('#jsPopap1').click(function(event) {
			$('#popap1').arcticmodal();
		});
		$('#jsPopap2').click(function(event) {
			$('#popap2').arcticmodal();
		});
		$('#jsPopap3').click(function(event) {
			$('#popap3').arcticmodal();
		});
		$('#jsPopap4').click(function(event) {
			$('#popap4').arcticmodal();
		});
		$('#jsPopap5').click(function(event) {
			$('#popap5').arcticmodal();
		});
		$('#jsPopap6').click(function(event) {
			$('#popap6').arcticmodal();
		});
		$('#jsPopap7').click(function(event) {
			$('#popap7').arcticmodal();
		});
		$('#jsPopap8').click(function(event) {
			$('#popap8').arcticmodal();
		});
	//reviews Hidden
		$("#reviewsBtn").click(
			function(){
				$("#reviewsHidden").slideToggle(300);
				$(this).css({'display':'none'});
			}
		);
	//modules
		$(".module__head").click(
			function(){
				$(this).next(".module__body").toggle(100);
			}
		);
	//slide function
	    $('a[href^="#"], *[data-href^="#"]').on('click', function(e){
	        e.preventDefault();
	        var t = 1000;
	        var d = $(this).attr('data-href') ? $(this).attr('data-href') : $(this).attr('href');
	        $('html,body').stop().animate({ scrollTop: $(d).offset().top }, t);
	    });

});