$(document).ready(function(){$("#accessLink").click(function(){$(".subscribe").slideToggle(200)})});


(function ()
{
	var days = [19, 19, 19, 19, 19, 19, 19];

    // var get_webinar_day  = function() { return ((new Date()).getDay()+1)%7; };
    var get_webinar_time  = function() { 
    	var time = PageTimers.convert_local_to_moscow(new Date());
    	var day = time.getDay();

    	if ( time.getHours() >= days[day] )
        {
    		time = PageTimers.time_add_days(time, 1);
            day = (day+1)%7;
        }

    	time.setHours(days[day]);
    	time.setMinutes(0);
    	time.setSeconds(0);

    	return time; 
    };

    var endDate = get_webinar_time();


    /*  Дата вебинара */
    
    PageTimers.add ( endDate, '', function(timer, date, expired) {
        if (expired) timer.date = get_webinar_time();
    
        var d = timer.date;
    
        var d1 = d;

        $('.webinar-date').html(
            d1.getDate() + ' ' + PageTimers.months_days2[d1.getMonth()]
        );
        $('.webinar-time').html(
            d1.getHours()
        );
    });
   
    /*  Дата вебинара  END  */


})();

