 $(document).ready(function(){
	 $('a[href^="#"], *[data-href^="#"]').on("click", function (o) {
		o.preventDefault();
		var t = $(this).attr("data-href") ? $(this).attr("data-href") : $(this).attr("href");
		$("html,body").stop().animate({
			scrollTop: $(t).offset().top
		}, 1e3)
	});
	$('#headerMailLink').click(function(){
		$('.header-subscribe').slideToggle(300);
	});
	$('#bottomMailLink').click(function(){
		$('.bottom-subscribe').slideToggle(300);
	});
	 
 });