$(document).ready(function(){
	//Modal
		$('#jsPopap1').click(function(event) {
			$('#popap1').arcticmodal();
		});
		$('#jsPopap2').click(function(event) {
			$('#popap2').arcticmodal();
		});
		$('#jsPopap3').click(function(event) {
			$('#popap3').arcticmodal();
		});
	//Modal form
		$('#form1Link').click(function(event) {
			$('#form1').arcticmodal();
		});
		$('#form2Link').click(function(event) {
			$('#form2').arcticmodal();
		});
	//slide function
	    $('a[href^="#"], *[data-href^="#"]').on('click', function(e){
	        e.preventDefault();
	        var t = 1000;
	        var d = $(this).attr('data-href') ? $(this).attr('data-href') : $(this).attr('href');
	        $('html,body').stop().animate({ scrollTop: $(d).offset().top }, t);
	    });

});