 $(document).ready(function(){
	$('#headerMailLink').click(function(){
		$('.header-subscribe').slideToggle(300);
	});
	$('#bottomMailLink').click(function(){
		$('.bottom-subscribe').slideToggle(300);
	});
	 
	$('.reviews__slider').slick({
		prevArrow: '<button class="arrow-left"></button>',
		nextArrow: '<button class="arrow-right"></button>'
	});
	 
	$('.reviews__open').readmore({
		collapsedHeight: 80,
		speed: 200,
		moreLink: '<a class="reviews__open-btn" href="#">Подробнее</a>',
		lessLink: '<a class="reviews__open-btn" href="#">Cкрыть</a>'
	});
 });