$(document).ready(function () {
	$('a[href^="#"], *[data-href^="#"]').on("click", function (r) {
		r.preventDefault();
		var t = $(this).attr("data-href") ? $(this).attr("data-href") : $(this).attr("href");
		$("html,body").stop().animate({
			scrollTop: $(t).offset().top
		}, 1e3)
	}),
	$("#reviewSlider").slick({
		adaptiveHeight: true,
		prevArrow: ".reviews__prev",
		nextArrow: ".reviews__next",
		// dots: !0,
		// appendDots: ".slider__dots",
		slidesToShow: 1
		// centerMode: true,
		// centerPadding: '200px'
		// responsive: [{
		// 	breakpoint: 767,
		// 	settings: {
		// 		slidesToShow: 1,
		// 		slidesToScroll: 1
		// 	}
		// }]
	}), 
	$("#partsSlider").slick({
		adaptiveHeight: true,
		prevArrow: ".parts__prev",
		nextArrow: ".parts__next",
		// dots: !0,
		// appendDots: ".parts__dots",
		slidesToShow: 3,
		slidesToScroll: 1,
		responsive: [{
			breakpoint: 767,
			settings: {
				slidesToShow: 1,
				slidesToScroll: 1
			}
		}]
	});
	$( "#accordion" ).accordion({
		active: false,
		collapsible: true
	});
});