$(document).ready(function () {
	$('a[href^="#"], *[data-href^="#"]').on("click", function (r) {
		r.preventDefault();
		var t = $(this).attr("data-href") ? $(this).attr("data-href") : $(this).attr("href");
		$("html,body").stop().animate({
			scrollTop: $(t).offset().top
		}, 1e3)
	});
	$( "#accordion" ).accordion({
		active: false,
		collapsible: true
	});
});