$(document).ready(function () {
	$('#modal1').click(function(event) {
		$('#popup1').arcticmodal();
	});
    $('#modal2').click(function(event) {
		$('#popup2').arcticmodal();
	});
	$('a[href^="#"], *[data-href^="#"]').on("click", function (e) {
		e.preventDefault();
		var r = $(this).attr("data-href") ? $(this).attr("data-href") : $(this).attr("href");
		$("html,body").stop().animate({
			scrollTop: $(r).offset().top
		}, 1e3)
	}), $("#reviewSlider").slick({
		prevArrow: ".slider__arrow-left",
		nextArrow: ".slider__arrow-right",
		dots: !0,
		appendDots: ".slider__dots",
		slidesToShow: 2,
		slidesToScroll: 2,
		responsive: [{
			breakpoint: 767,
			settings: {
				slidesToShow: 1,
				slidesToScroll: 1
			}
		}]
	})
});
