$(document).ready(function () {
	$('#modal, #modal2').click(function(event) {
		$('#popup').arcticmodal();
	});
	$('a[href^="#"], *[data-href^="#"]').on("click", function (e) {
		e.preventDefault();
		var r = $(this).attr("data-href") ? $(this).attr("data-href") : $(this).attr("href");
		$("html,body").stop().animate({
			scrollTop: $(r).offset().top
		}, 1e3)
	})
});
